var Datastore = require("nedb");
var spromise  = require("spromise");

var ready = spromise.defer();
var db = {};

db.companies = new Datastore({
  filename: "storage/companies.db",
  autoload: true,
  onload: function(err) {
    if ( err ) {
      ready.reject(err);
    }
    else {
      ready.resolve(db);
    }
  }
});

db.companies.loadDatabase();
db.companies.ready = ready.promise;
module.exports = db.companies;
