var Datastore = require("nedb");
var spromise  = require("spromise");

var ready = spromise.defer();
var db = {};

db.people = new Datastore({
  filename: "storage/people.db",
  autoload: true,
  onload: function(err) {
    if ( err ) {
      ready.reject(err);
    }
    else {
      ready.resolve(db);
    }
  }
});

db.people.loadDatabase();
db.people.ready = ready.promise;
module.exports = db.people;
