var Datastore = require("nedb");
var spromise  = require("spromise");

var ready = spromise.defer();
var db = {};

db.positions = new Datastore({
  filename: "storage/positions.db",
  autoload: true,
  onload: function(err) {
    if ( err ) {
      ready.reject(err);
    }
    else {
      ready.resolve(db);
    }
  }
});

db.positions.loadDatabase();
db.positions.ready = ready.promise;
module.exports = db.positions;
