var JOBSEEKER = require("services/jobSeeker");


function jobSeekerRest(app) {
  var jobSeeker = new JOBSEEKER();

  app.post("/jobSeeker/company", function(req, res) {
    jobSeeker.company(req.body)
      .done(function() {
        res.send();
      })
      .fail(function(err) {
        res.send(500, err);
      });
  });

  app.get("/jobSeeker/companylist", function(req, res) {
    jobSeeker.companylist(req.body)
      .done(function() {
        res.send();
      })
      .fail(function(err) {
        res.send(500, err);
      });
  });



  app.post("/jobSeeker/disconnect/:cid", function(req, res) {
    jobSeeker.disconnect(req.params.cid)
      .done(function() {
        res.send();
      })
      .fail(function(err) {
        res.send(500, err);
      });
  });

}


module.exports = jobSeekerRest;

